mapper.select:
select id,username,phone,createdat from litemall_merchant where 1=1

    and username = #{username}


    and phone = #{phone}

    ORDER BY createdat desc

mapper.insert:
      INSERT INTO litemall_merchant (
            username,
            phone,
            createdat
          )
          VALUES
            (

              #{username},


              #{phone},


              #{createdat}

) ;

mapper.select:
SELECT COUNT(0) FROM litemall_merchant
where 1=1

    and phone = #{phone}
